    let sampleRole = {
        name            : "Sample Privilege",
        description     : "Sample description for the privilege",
        members         : [userId],
        accessRoutes    : [
            {route : route.accessedByUser, method : "PUT"}
        ]
    };
    sendRequest(url.role.create(),'post',sampleRole,201,function (err,res) {
